const fontSize = {
  large:"1.5rem",
  medium:"1.25rem",
  base:"1rem",
  small:"0.75rem",
}

const ColorTheme = {
  background:"#fff",
  fontPrimary:"#000",
  fontSecondary:"#999",
  hover:"#ddd",
}

const ItemSize = {
  large:"width:1024px;",
  base:"width:780px;",
  small:"width:400px;",
}

const theme = {
  fontSize,
  ColorTheme,
  ItemSize
}

export default theme;
