#!/usr/bin/env node

const { exec } = require("node:child_process");

const sc = require("samcolors");

const file = process.argv[2] ?? null;

if (file === null) {
  console.log(sc.bold(sc.red("You need to specify the file to exec")));
  process.exit(1);
}

exec(`npx ts-node ${file}`, (error, stdout, stderr) => {
  if (error) {
    console.error(`Error executing script ${file}: ${error.message}`);
    return;
  }
  if (stderr) {
    console.error(stderr);
    return;
  }

  console.log(stdout);
});
